INSERT INTO `tb_cross_talk` VALUES (5, '小区门口买鸭脖，等候时和卖鸭脖的武汉小伙儿聊天：“干这行开心吗？”\r\n小伙子爽快地回答：“当然开心，想砍谁的脑袋就砍谁的脑袋，像皇上一样。”\r\n说话间，手起刀落，一颗鸭头在砧板上咕噜噜地滚了很远。。。\r\n我汗。。。', 2, '007', '2018-9-12 09:49:00');
